elements.element:
	align:'right',
	editable:true,
	editor:new Ext.form.NumberField({
		maxLength:12, allowBlank:true,
		allowNegative: false
	})

	editable:true,
	editor:new Ext.form.TriggerField({
		maxLength:100,
		myeditable:false, allowBlank:true,
		triggerClass:'x-form-search-trigger', 
		onTriggerClick: function() {
			if (this.menu == null) {
				var selcfg = {config};
				this.menu = Jxstar.createComboMenu(this);
				Jxstar.createComboGrid(selcfg, this.menu, 'node_{funid}_editgrid');
			}
			this.menu.show(this.el);
		}
	})

	editable:true,
	editor:new Ext.form.TriggerField({
		maxLength:100,
		myeditable:false, allowBlank:true,
		triggerClass:'x-form-search-trigger', 
		onTriggerClick: function() {
			var selcfg = {config};
			Jxstar.createSelectWin(selcfg, this, 'node_{funid}_editgrid');
		}
	})

	align:'center',
	editable:true,
	editor:new Ext.form.ComboBox({
		store: new Ext.data.SimpleStore({
			fields:['value','text'],
			data: {name}Data
		}),
		emptyText: jx.star.select,
		mode: 'local',
		triggerAction: 'all',
		valueField: 'value',
		displayField: 'text',
		editable:false, allowBlank:true,
		value: {name}Data[0][0]
	}),
	renderer:function(value){
		for (var i = 0; i < {name}Data.length; i++) {
			if ({name}Data[i][0] == value)
				return {name}Data[i][1];
		}
	}

	align:'center',
	renderer:function(value){
		for (var i = 0; i < {name}Data.length; i++) {
			if ({name}Data[i][0] == value)
				return {name}Data[i][1];
		}
	}

	editable:true,
	editor:new Ext.form.DateField({
		format: 'Y-m-d', allowBlank:true,
		minValue: '1900-01-01'
	}),
	renderer:function(value) {
		return value ? value.format('Y-m-d') : '';
	}

	editable:true,
	editor:new Ext.form.Checkbox(),
	renderer:function(value) {
		return value=='1' ? jx.base.yes : jx.base.no;
	}
